import React, { useState } from "react";
import SideBar from "./components/SideBar";
import Main from "./components/Main";
import "./App.css";

export default function App() {
  const [PersonalData, setPersonalData] = useState({
    name: "",
    title: "",
    cell: "",
    email: "",
    linkedin: "",
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setPersonalData((prev) => ({
      ...prev,
      [name]: value,
    }));
  }

  function submitSkill(e) {
    e.preventDefault();
    handleSkill();
  }

  const [skillsData, setSkillsData] = useState([
    {
      skillName: "",
    },
  ]);

  console.log(skillsData);
  function handleSkill(e) {
    const { value, name } = e.target;
    setSkillsData((prev) =>([...prev, prev.push({[name]:value})]));
  }

  return (
    <div className="app-container">
      <SideBar
        nameValue={PersonalData.name}
        nameHolder="name"
        titleValue={PersonalData.title}
        titleHolder="title"
        cellValue={PersonalData.cell}
        cellHolder="cell"
        emailValue={PersonalData.email}
        emailHolder="email"
        linkedinValue={PersonalData.linkedin}
        linkedinHolder="linkedin"
        onChange={handleChange}
        onSubmitSkill={submitSkill}
        skillValue={skillsData.SkillName}
        skillHolder="skillName"
      />
      <Main
        name={PersonalData.name}
        title={PersonalData.title}
        cell={PersonalData.cell}
        email={PersonalData.email}
        linkedin={PersonalData.linkedin}
        skills={skillsData.map((skill) => skill)}
      />
    </div>
  );
}
