import React, { useState } from "react";
import SideBar from "./components/SideBar";
import Main from "./components/Main";
import "./App.css";

export default function App() {
  const [PersonalData, setPersonalData] = useState({
    personName: "",
    title: "",
    cell: "",
    email: "",
    linkedIn: "",
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setPersonalData((prev) => ({
      ...prev,
      [name]: value,
    }));
  }
  return (
    <div className="app-container">
      <SideBar
        nameValue={PersonalData.personName}
        nameHolder="name"
        titleValue={PersonalData.title}
        titleHolder="title"
        cellValue={PersonalData.cell}
        cellHolder="cell"
        emailValue={PersonalData.email}
        emailHolder="email"
        linkedinValue={PersonalData.linkedIn}
        linkedinHolder="linkedIn"
        onChange={handleChange}
      />
      <Main
        name={PersonalData.personName}
        title={PersonalData.title}
        cell={PersonalData.cell}
        email={PersonalData.email}
        linkedin={PersonalData.linkedIn}
      />
    </div>
  );
}
