import React from "react";

export default function Hobbies(props) {
  function handleClickAdd() {
    let fieldValue = skillsInputField.current.value;
    if (skillsInputField && !props.skill.includes(fieldValue)) {
      props.setSkill((prev) => [...prev, props.skillValue]);
    }
    skillsInputField.current.value = "";
  }

  return (
    <div className="Hobbies">
      <p className="hobbies-info">Update Hobbies Info:</p>
      <label htmlFor="hobbies">Interests:</label>
      <div className="hobbies-input-section">
        <input
          type="text"
          id="hobbies"
          placeholder="Enter Hobby"
          className="personal-inputs"
        />
        <span>Add Hobby</span>
        <button className="add-hobby-btn">+</button>
      </div>
    </div>
  );
}
