import React, { useRef } from "react";

export default function WorkExperience(props) {
  const companyField = useRef();
  const jobRole = useRef();
  const from = useRef();
  const to = useRef();
  const responsibilities = useRef();

  function handleAddExperience() {
    // props.setExperience((prev) => [
    //   ...prev,
    //   {
    //     company: companyField.current.value,
    //     role: jobRole.current.value,
    //     roleFrom: from.current.value,
    //     roleTo: to.current.value,
    //     roleDuties: responsibilities.current.value,
    //   },
    // ]);

    console.log(companyField.current.value);
  }
  return (
    <div className="work-experience-info">
      <p className="personal-info-title">Update Work Experience Details:</p>
      <label htmlFor="company">Company Name:</label>
      <input
        type="text"
        id="company"
        placeholder="Company Name"
        className="personal-inputs"
        // value={props.companyValue}
        // onChange={props.onChange2}
        name={props.companyHolder}
        ref={companyField}
      />
      <label htmlFor="job_role">Job Role:</label>
      <input
        type="text"
        id="job-role"
        placeholder="Enter Job Title"
        className="personal-inputs"
        // value={props.roleValue}
        // onChange={props.onChange2}
        name={props.roleHolder}
        ref={jobRole}
      />

      <label htmlFor="fromDate">From:</label>
      <input
        type="date"
        id="date"
        placeholder="Enter Job Title"
        className="personal-inputs"
        // value={props.roleFromValue}
        onChange={props.onChange2}
        name={props.roleFromHolder}
        ref={from}
      />
      <label htmlFor="date">To:</label>
      <input
        type="date"
        id="toDate"
        placeholder="Enter Job Title"
        className="personal-inputs"
        // value={props.roleToValue}
        // onChange={props.onChange2}
        name={props.roleToHolder}
        ref={to}
      />
      <div className="responsibilities-info">
        <label htmlFor="responsibilities">Responsibilties:</label>
        <input
          id="responsibilities"
          placeholder="Enter Responsibilities"
          className="personal-inputs"
          // value={props.roleDutiesValue}
          // onChange={props.onChange2}
          name={props.roleDutiesHolder}
          ref={responsibilities}
        />
        <button className="add-responsibilities-btn">+ Add Role</button>
      </div>
      <span>Add Work Experience:</span>
      <button className="add-work-experience-btn" onClick={handleAddExperience}>
        +
      </button>
    </div>
  );
}
